import React, { useState } from 'react';
import './NewListing.css';

function NewListing() {
    const [selectedOption, setSelectedOption] = useState('');
  const [financingOptions, setFinancingOptions] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [province, setProvince] = useState('');
  const [canton, setCanton] = useState('');
  const [distrito, setDistrito] = useState('');
  const [propertyTitle, setPropertyTitle] = useState('');
  const [propertyDescription, setPropertyDescription] = useState('');
  const [propertyPrice, setPropertyPrice] = useState('');

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleCheckboxChange = (option) => {
    // Verifica si la opción ya está seleccionada
    if (financingOptions.includes(option)) {
      // Si está seleccionada, quítala de las opciones
      setFinancingOptions(financingOptions.filter((item) => item !== option));
    } else {
      // Si no está seleccionada, agrégala a las opciones
      setFinancingOptions([...financingOptions, option]);
    }
  };

  const handleImageChange = (event) => {
    const files = event.target.files;
    setImageFiles([...imageFiles, ...files]);
  };

  const handleAddProperty = () => {
    // Validar campos antes de agregar la propiedad
    if (!propertyTitle || !propertyDescription || !propertyPrice) {
      alert('Por favor, completa todos los campos obligatorios.');
      return;
    }

    // Aquí puedes realizar la lógica para agregar la propiedad
    // Puedes enviar los datos al servidor, almacenar en el estado global, etc.

    // Limpiar los campos después de agregar la propiedad
    setPropertyTitle('');
    setPropertyDescription('');
    setPropertyPrice('');
    setSelectedOption('');
    setFinancingOptions([]);
    setImageFiles([]);
    setProvince('');
    setCanton('');
    setDistrito('');

    alert('Propiedad agregada con éxito!');
  };


  return (
    <div className='main-container'>
      <form className='form-container'>
        <div className='design-container'>
          <h1>Descripción de la propiedad</h1>

          <div className='form-group'>
            <label>Titulo de la propiedad</label>
            <input type='text' />
          </div>

          <div className='form-group'>
            <label>Descripción adicional</label>
            <textarea rows='4'></textarea>
          </div>

          <div className='form-group'>
            <p>Precio de la propiedad</p>
            <label>Precio</label>
            <input type='text' />
          </div>

          <div className='form-group'>
            <label htmlFor='options'>Posibles métodos de pago:</label>
            <select id='options' value={selectedOption} onChange={handleSelectChange}>
              <option value=''>Selecciona...</option>
              <option value='carro'>Carro</option>
              <option value='propiedad'>Propiedad</option>
              <option value='moto'>Moto</option>
              <option value='otro'>Otro</option>
            </select>

            {selectedOption && (
              <p>Tu selección es: {selectedOption}</p>
            )}
          </div>
          <div className='form-group'>
            <label>Opciones de financiamiento:</label>
            <div className='checkbox-group'>
              <input
                type='checkbox'
                value='contado'
                checked={financingOptions.includes('contado')}
                onChange={() => handleCheckboxChange('contado')}
              />
              <label>Contado</label>

              <input
                type='checkbox'
                value='financiamiento'
                checked={financingOptions.includes('financiamiento')}
                onChange={() => handleCheckboxChange('financiamiento')}
              />
              <label>Financiamiento</label>
              {/* Agrega más opciones según sea necesario */}
            </div>
          </div>
          <div className='form-group'>
            <label>Otro:</label>
            <input type='text' />
          </div>

        </div>

        <div className='media-container'>
          <h1>Fotos de la propiedad</h1>
          <input type='file' multiple onChange={handleImageChange} />          
          <div className='image-preview-container'>
            {imageFiles.map((file, index) => (
              <img
                key={index}
                src={URL.createObjectURL(file)}
                alt={`Imagen ${index + 1}`}
                className='image-preview'
              />
            ))}
          </div>
        </div>

        <div className='location-container'>
        <h1>Ubicación de la propiedad</h1>

        <div className='form-group'>
            <label>Provincia</label>
            <input type='text' value={province} onChange={(e) => setProvince(e.target.value)} />
        </div>

        <div className='form-group'>
            <label>Cantón</label>
            <input type='text' value={canton} onChange={(e) => setCanton(e.target.value)} />
        </div>

        <div className='form-group'>
            <label>Distrito</label>
            <input type='text' value={distrito} onChange={(e) => setDistrito(e.target.value)} />
        </div>
        <div className='form-group'>
            <label>Direccion extra opcional</label>
            <textarea rows='4'></textarea>
          </div>


        </div>
        <div className='form-group'>
          <button type='button' onClick={handleAddProperty}>
            Agregar Propiedad
          </button>
        </div>
      </form>
    </div>
  );
}

export default NewListing;
