import React, { useState } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import './NewListing.css';
import SuccessModal from './SuccessModal';
import { addProperty } from '../api/property';

function NewListing() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [selectedOption, setSelectedOption] = useState('');
  const [financingOptions, setFinancingOptions] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [province, setProvince] = useState('');
  const [canton, setCanton] = useState('');
  const [distrito, setDistrito] = useState('');
  const [direccion, setDireccion] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [redirectTo, setRedirectTo] = useState(null);

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleCheckboxChange = (option) => {
    if (financingOptions.includes(option)) {
      setFinancingOptions(financingOptions.filter((item) => item !== option));
    } else {
      setFinancingOptions([...financingOptions, option]);
    }
  };

  const handleImageChange = (event) => {
    const files = event.target.files;
    setImageFiles([...imageFiles, ...files]);
  };

  const handleAddProperty = async (event) => {
    event.preventDefault();

    try {
      const formData = new FormData();
      imageFiles.forEach((file) => {
        formData.append('images', file);
      });

      const response = await axios.post('http://localhost:9000/upload', formData);
      const imageUrls = response.data;

      const propertyData = {
        title: title,
        description: description,
        price: price,
        paymentMethod: selectedOption,
        financingOptions: financingOptions,
        province: province,
        canton: canton,
        distrito: distrito,
        images: imageUrls,
        direccion: direccion,
        // ownerID: token, // Asegúrate de incluir el ownerID según tu lógica
      };

      const res = await addProperty(propertyData);
      console.log(res);

      setTitle('');
      setDescription('');
      setPrice('');
      setSelectedOption('');
      setFinancingOptions([]);
      setImageFiles([]);
      setProvince('');
      setCanton('');
      setDistrito('');
      setDireccion('');

      setShowSuccessModal(true);

      setTimeout(() => {
        setShowSuccessModal(false);
        // Establecer la redirección después de agregar la propiedad
        setRedirectTo('/ruta-deseada');
      }, 3000);
    } catch (error) {
      console.error('Error al agregar la propiedad:', error);
    }
  };

  // Redirigir si redirectTo tiene un valor
  if (redirectTo) {
    return <Navigate to={redirectTo} />;
  }

  return (
    <div className='main-container'>
      {/* Resto del código... */}
    </div>
  );
}

export default NewListing;
