import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import './RegisterForm.css';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import { registerRequest } from '../api/auth';
import Alert from './Alert';

function RegisterForm() {
  const [firstName, setFirstName] = useState('');
  const [email, setEmail] = useState('');
  const [contrasenna, setPassword] = useState('');
  const [telefono, setTelefono] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [alert, setAlert] = useState(null);

  const handleClickShowPassword = () => setShowPassword((show) => !show);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleButtonClick = async () => {
    try {
      const requestData = {
        name: firstName,
        phone: telefono,
        email: email,
        password: contrasenna
      };

      const res = await registerRequest(requestData);

      // Verificar si la respuesta tiene un mensaje
      if (res && res.message) {
        // Registro exitoso
        setAlert('Registro exitoso');
      } else {
        // Error en el registro
        console.error('Error en el registro:', res.data.message);
        setAlert(`Error: ${res.data.message}`);
      }
    } catch (error) {
      console.error('Error al registrar:', error);
      setAlert('Error al registrar. Por favor, verifica tus datos.');
    }
  };

  return (
    <>
      <div className='signUp_container'>
        <div className='register_container '>
          <Box
            component='form'
            sx={{
              '& .MuiTextField-root': { m: 2, width: '45ch' },
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'left',
              alignContent: 'left',
              marginLeft: '-200px',
            }}
            noValidate
            autoComplete='off'
          >
            <h1>Sign In to your Account</h1>
            {/* Resto del código de tu formulario */}
          </Box>
        </div>
        <div className='logoBtn'>
          <img
            src='./images/LogoIntercambio.png'
            style={{ width: '400px', height: 'auto', alignContent: 'center', marginLeft: '100px' }}
          />
          <FormGroup>
            <FormControlLabel required control={<Checkbox />} label='Acepta los Términos y Condiciones' />
          </FormGroup>
          <Button buttonStyle='btn--outline' onClick={handleButtonClick}>
            Crear cuenta
          </Button>
        </div>
        {alert && <Alert message={alert} onClose={() => setAlert(null)} />}
      </div>
    </>
  );
}

export default RegisterForm;
