// PropertyCard.js
import React, { useState } from 'react';
import './PropertyCard.css'; // Archivo de estilos específico para PropertyCard
import { Link } from 'react-router-dom';
import { Dialog, DialogTitle, DialogContent, Button } from '@mui/material';

const PropertyCard = ({ property }) => {
  const [open, setOpen] = useState(false);

  const handleButtonClick = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className="property-card-container">
      <h2>{property.title}</h2>
      <p>{property.description}</p>
      <p>Precio: {property.price}</p>
      {/* Agrega el botón con el evento onClick */}
      <button className="details-button" onClick={handleButtonClick}>
        Ver Detalles
      </button>
          <Link to={`/property-details/${property._id}`}>
            <Button onClick={handleClose}>Ir a Detalles</Button>
          </Link>
          <Button onClick={handleClose}>Cerrar</Button>
    </div>
  );
};

export default PropertyCard;
