// PropertyCard.js
import React from 'react';
import './PropertyCard.css'; // Archivo de estilos específico para PropertyCard

const PropertyCard = ({ property, onClickButton }) => {
  const handleButtonClick = () => {
    onClickButton(property._id);
  };

  return (
    <div className="property-card-container">
      <h2>{property.title}</h2>
      <p>{property.description}</p>
      <p>Precio: {property.price}</p>
      {/* Agrega el botón con el evento onClick */}
      <button className="details-button" onClick={handleButtonClick}>
        Ver Detalles
      </button>
    </div>
  );
};

export default PropertyCard;
