// UserReport.js
import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import { Button } from './Button';
import './UserReport.css'; // Agrega tus estilos según sea necesario

function UserReport({ onReportSubmit }) {
  const [reportReason, setReportReason] = useState('');

  const handleReportReasonChange = (event) => {
    setReportReason(event.target.value);
  };

  const handleReportSubmit = () => {
    // Aquí puedes realizar alguna acción con el motivo del reporte
    // Puedes enviarlo al servidor, mostrar una alerta, etc.
    onReportSubmit(reportReason);
    // También puedes limpiar el estado o cerrar el componente después de enviar el reporte.
    setReportReason('');
  };

  return (
    <div className='user-report-container'>
      <h2>Reportar Usuario</h2>
      <Box sx={{ '& > :not(style)': { m: 1 } }}>
        <FormControl sx={{ m: 1, width: '25ch' }} variant='standard'>
          <InputLabel htmlFor='report-reason'>Motivo del Reporte</InputLabel>
          <Input
            id='report-reason'
            value={reportReason}
            onChange={handleReportReasonChange}
          />
        </FormControl>
      </Box>
      <div className='btn-report'>
        <Button buttonStyle='btn--outline' onClick={handleReportSubmit}>
          Enviar Reporte
        </Button>
      </div>
    </div>
  );
}

export default UserReport;
